            <section className="app__comments">
                <div className="app__comments-displayed">
                {props.mainVideo.commentsArr.map((users) => {
                return <CommentsCard id={users.id} name={users.name} timestamp={users.timestamp} comment={users.comment} />
                   }
                )}
                </div>
            </section>
        )

}


