import React from 'react';
import travelPro from './../../assets/Images/video-list-1.jpg'
import lesHouches from './../../assets/Images/video-list-2.jpg'
import travelHealth from './../../assets/Images/video-list-3.jpg'
import cheapAirline from './../../assets/Images/video-list-4.jpg'
import romanticBreak from './../../assets/Images/video-list-5.jpg'
import perfectAccomodations from './../../assets/Images/video-list-6.jpg'
import cruisingDestination from './../../assets/Images/video-list-7.jpg'
import trainTravel from './../../assets/Images/video-list-8.jpg'


class Aside extends React.Component {
    constructor(videoInfo) {
        super(videoInfo);

        this.state = {
            sideVideo: [
                {id: 1, title: 'Become A Travel Pro In One Easy Lesson...', channel: 'Scotty Cranmer', image:{travelPro} },
                {id: 2, title: 'Les Houches The Hidden Gem Of The...', channel: 'Scotty Cranmer', image:{lesHouches} },
                {id: 3, title: 'Travel Health Useful Medical Information...', channel: 'Scotty Cranmer', image:{travelHealth} },
                {id: 4, title: 'Cheap Airline Tickets Great Ways To Save', channel: 'Emily Harper', image:{cheapAirline} },
                {id: 5, title: 'Take A Romantic Break In A Boutique Hotel', channel: 'Ethan Owen', image:{romanticBreak} },
                {id: 6, title: 'Choose The Perfect Accommodations', channel: 'Lydia Perez', image:{perfectAccomodations} },
                {id: 7, title: 'Cruising Destination Ideas', channel: 'Timothy Austin', image:{cruisingDestination} },
                {id: 8, title: 'Train Travel On Track For Safety', channel: 'Scotty Cranmer', image:{trainTravel} },
            ],
        }
    }

    render() {
        return (
            <section className="app__aside-videos">
                <h3 class="app__aside-header">NEXT VIDEO</h3>
                <div class="app__aside-container">
                    {this.state.sideVideo.map(videoInfo => <card key={this.state.id} content={this.state.title} channel={this.state.channel} image={this.state.image}/>)}
                </div>
            </section>
        );
      }
    }

    export default Aside;
