import React from 'react';
import HighlightsCard from './../HighlightsCard/HighlightsCard';

class Highlights extends React.Component {

    state =  {

        mainVideo: [
            {title: 'BMX Rampage: 2018 Highlights', channel:'By Red Cow', date: '12/18/2018', views: '1,001,023', likes: '110,985', duration: '0:00/42', description:'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title'
        }
        ]
    }
    render() {

        return (
            <section className="app__highlights">
                <div className="app__highlights-container">
                <img className="app__highlights-viewsicon"src={process.env.PUBLIC_URL + '/assets/Icons/SVG/Icon-views.svg'} alt="maginifying glass" />
                <img className="app__highlights-likesicon"src={process.env.PUBLIC_URL + '/assets/Icons/SVG/Icon-likes.svg'} alt="heart icon" />

                {this.state.mainVideo.map((videoInfo) => <HighlightsCard key={videoInfo.id} title={videoInfo.title} channel={videoInfo.channel} date={videoInfo.date} views={videoInfo.views} likes={videoInfo.likes} description={videoInfo.description}/>)}
                </div>
                </section>
        )
    }
}


export default Highlights
