import React from 'react'; 
// import { render } from '@testing-library/react';

class Comments extends React.Component {// function Comments(props) {


    render() {
        return (
            <section className="app__comments">
                <div className="app__comments-displayed">
                        <h3 className="app__comments-username">{this.props.mainVideo.commentsArr.name}</h3>
                        <p className="app__comments-timestamp">{this.props.mainVideo.commentsArr.timestamp}</p>
                        <p className="app__comments-comment">{this.props.mainVideo.commentsArr.comment}</p>
                        {this.state.mainVideo.commentsArr.map((users) => {
                         <Comments id={users.id} name={users.name} timestamp={users.timestamp} comment={users.comment}
                    />})}
                </div>
            </section>
        )

}

}

    export default Comments
